import React, { useState } from 'react';
import './createpost.css';

export default function CreatePost() {
  
    const [body, setBody] = useState("");
    const [image, setImage] = useState("");
  
    return <div>
        <form className='form1'>
            <h1>Create Post</h1>
            <div class="formcontainer">
                <hr />
                <div class="containerY">
                    <label htmlFor="uname"><strong>Description</strong></label>
                    <input type="text" value={body} onChange={(event)=> event.target.value}  placeholder="Add description" name="uname" required className='descriptionY'/>
                    <label htmlFor="psw" style={{marginRight: '10px'}}><strong>Add image</strong></label>
                    <input type="file" value={image} onChange={(event)=> console.log(event.target.files)} className='upload' required />
                </div>
                <button className='btncreate' type="submit">Create Post</button>
                
            </div>
        </form>
    </div>;
}
