import React, { useState, useEffect } from 'react';
import MediaFeed from './MediaFeed';


export default function AllPosts() {

    const [data, setData] = useState([])
    useEffect(() => {
        fetch('/allpost', {
            headers: {
                "Authorization": "Bearer " + localStorage.getItem("jwt")
            }
        }).then(res =>{
             res.json()})
            .then(result => {
                console.log(result)
                setData(result.posts);
            })
    }, [])
    return( 
    <div>
        {
            data.map(item => {
              <MediaFeed
              key={item._id}
              image={item.photo}
              body = {item.body}
              />
            }
            )
        }
    </div>
)
}
