import React, { useState } from 'react';
import './createpost.css';

export default function CreatePost() {
  
    const [body, setBody] = useState("");
    const [image, setImage] = useState("");
    const [url, setUrl] = useState("");

    const postDetails = ()=>{
        const data = new FormData()
        data.append("file",image)
        data.append("upload_preset","socialmediaapp")
        data.append("cloud_name","abcdefu")
        fetch("https://api.cloudinary.com/v1_1/abcdefu/image/upload",{
            method:"post",
            body:data
        })
        .then(res=>res.json())
        .then(data=>{
           setUrl(data.url);
        })
        .catch(err=>{
            console.log(err)
        })
       
        fetch("/createpost", {
			method: "post",
			headers: {
				"Content-Type": "application/json"
			},
			body: JSON.stringify({
                body,
				pic:url,
			})
		}).then(res => res.json())
			.then(data => {
				if (data.error) {
					//M.toast({ html: data.error, classes: "#c62828 red darken-3" })
				}
				else {
					//M.toast({ html: data.message, classes: "#43a047 green darken-1" })
					history('/mainpage');
				}
			}).catch(err => {
				console.log(err)
			})
     
    }
  
    return <div>
        <form className='form1'>
            <h1>Create Post</h1>
            <div class="formcontainer">
                <hr />
                <div class="containerY">
                    <label htmlFor="uname"><strong>Description</strong></label>
                    <input type="text" value={body} onChange={(event)=> setBody(event.target.value)}  placeholder="Add description" name="uname" className='descriptionY'/>
                    <label htmlFor="psw" style={{marginRight: '10px'}}><strong>Add image</strong></label>
                    <input type="file" onChange={(event)=> setImage(event.target.files[0])} className='upload' required />
                </div>
                <button className='btncreate' type="submit" onClick={()=>postDetails()}>Create Post</button>
                
            </div>
        </form>
    </div>;
}
