import React, { useState, useEffect } from 'react';
import MediaFeed from './MediaFeed';


export default function AllPosts() {

    const [data, setData] = useState([])
    useEffect(()=>{
        fetch('/allpost',{
            headers:{
                "Authorization":"Bearer "+localStorage.getItem("jwt")
            }
        }).then(res=>res.json())
        .then(result=>{
            console.log(result)
            setData(result.posts)
        })
     },[])
    return( 
    <div style={{ display: "grid", gridTemplateColumns: "repeat(3, minmax(210px, 1fr))", gridTemplateRows: "auto" ,gridGap: "5rem", alignItems:"start"}}>
        {
            data.map(item => {
               
                return (
                  <div >
                  <MediaFeed
                  key={item._id}
                  image={item.photo}
                  body = {item.body}
                  />
                  </div>)
            }
            )
        }
    </div>
)
}
